import React, {useContext, useState} from "react";
import {payoutsList} from "../PayoutsList";
import BottomNav from "./BottomNav";
import {FormDispatchContext} from "../FormContext";

const SelectPayout = () => {

    const [payout, setPayout] = useState(null)
    const dispatch = useContext(FormDispatchContext)
    const handleChange = (e) => setPayout(e.target.value)

    const handleNext = () => {
        const selectedPayout = payoutsList.find(item => item.id === payout.id)

        dispatch({
            type: 'SET_FORM_DATA',
            payload: {payoutId: payout}
        })

        if(selectedPayout.status !== 'APPROVED'){
            // SET FORM TYPE ASK
        }

        //go to next step

    }

    return <>
        <h1>Wybierz wypłatę</h1>
        {payoutsList.map(payout => <div style={{margin: '20px 0'}}>
            <input type={"radio"} value={payout.id} name={'transaction'} id={payout.id}
                   onChange={handleChange}/>
            <label htmlFor={payout.id}>{payout.date} - {payout.amount} ({payout.status})</label>
        </div>)}
        <BottomNav onNext={handleNext}/>
    </>
}

export default SelectPayout
