import React from "react";

const Step = ({state, setState})=> {
    const {currentStep, flow} = state

    const handleStateChange = (data) => {
        setState({
            ...state,
            data: {
                ...state.data,
                ...data}

        })
    }

    const renderCurrentStep = () => {

        const stepToRender =  state.steps.find(step => step.name === state.flow[currentStep])

        const Component = stepToRender.component

        return <Component state={state} onChange={handleStateChange}/>
    }

    const handleNextBtnClick = () => {
        setState({
            ...state,
            currentStep: currentStep + 1
        });
    }

    const handlePrevBtnClick = () => {
        setState({
            ...state,
            currentStep: currentStep - 1
        });
    }
    return <>
        <br />
        <br />
        <br />

        <pre>
        {JSON.stringify(state, null, 2)}
        </pre>
        <br />
        <br />
        <br />
        <pre>
        </pre>
        <br />
        {currentStep +1} z {flow.length}
        <br />
        {renderCurrentStep()}
        <br />
        {currentStep >0 && <button onClick={handlePrevBtnClick}>WroÄ‡ </button>}
        <br />
        {currentStep < flow.length -1 && <button onClick={handleNextBtnClick}>Dalej </button>}
    </>
}

export default Step
