import React, {useContext} from "react";
import {FormDispatchContext, FormStateContext} from "../FormContext";
import {STEP_DETAILS, STEPS} from "../Steps";
import SelectForm from "./SelectForm";

const StepContent = () => {
    const state = useContext(FormStateContext);





    const renderContent = () => {
        const currentStep = state.currentStep
        const currentStepName = state.flow[currentStep]
        const currentStepObject =
            STEP_DETAILS.find(step => step.name === currentStepName)
        const Content = currentStepObject.component
        return <Content />
    }

    const getStepContent = (stepName) => {
        switch (stepName){
            case STEPS.SELECT_REASON:
                return <SelectForm />
        }
    }
    return( <>
        {getStepContent(currentStepName)}
    </>)
}

export default StepContent
