import Step from "./Components/Step";
import {useState} from "react";
import YouHaveToAgree from "./Components/YouHaveToAgree";
import BadPucharse1 from "./Components/BadPucharse1";
import BadPucharse2 from "./Components/BadPucharse2";
import BadPucharse3 from "./Components/BadPucharse3";
import PurchaseDetails from "./Components/PurchaseDetails";
import TransactionDetails from "./Components/TransactionDetails";


function App() {


    const defaultFormState = {

        data: {
            purchaseReturned: false,
            companyPurchase: false,
            transactionId: '',
            orderNumber: ''
        },
        currentStep: 0,
        flow: ['PurchaseDetails', 'TransactionDetails']
    }


    const [formState, setFormState] = useState(defaultFormState)


    const steps = [
        {
            name: 'PurchaseDetails',
            component: PurchaseDetails,
            onSubmit: (state, setState) => {

                if (state.data.purchaseReturned && state.data.companyPurchase) {
                    setState({
                        ...state,
                        currentStep: 0,
                        flow: ['BadPurchase3']
                    })
                    return
                }
                if (state.data.purchaseReturned) {
                    setState({
                        ...state,
                        currentStep: 0,
                        flow: ['BadPurchase1']
                    })
                    return
                }
                if (state.data.companyPurchase) {
                    setState({
                        ...state,
                        currentStep: 0,
                        flow: ['BadPurchase2']
                    })
                    return
                }

                setState(state)
            }

        },
        {
            name: 'TransactionDetails',
            component: TransactionDetails,
        },
        {
            name: 'Attachment',
            component: YouHaveToAgree
        },
        {
            name: 'Thank You',
            component: YouHaveToAgree
        },
        {
            name: 'BadPurchase1',
            component: BadPucharse1
        },
        {
            name: 'BadPurchase2',
            component: BadPucharse2
        },
        {
            name: 'BadPurchase3',
            component: BadPucharse3
        },

    ]

    return (
        <div className="App" style={{padding: "50px 100px"}}>
            <Step state={formState} setState={setFormState} steps={steps}/>
        </div>
    );
}

export default App;
