import React, {useContext, useState} from "react";
import {FormDispatchContext} from "../FormContext";

const PurchaseDetails = () => {

    const [purchaseReturned, setPurchaseReturned] = useState(false)
    const [companyPurchase, setCompanyPurchase] = useState(false)
    const dispatch = useContext(FormDispatchContext);


    const handleChangePurchaseReturned = (e) => {
        setPurchaseReturned(e.target.checked)
    }

    const handleChangeCompanyPurchase = (e) => {
        setCompanyPurchase(e.target.checked)
    }
    const handleNext = () => {
        if(purchaseReturned || companyPurchase){
            dispatch('SHOW_FORM_EROR_PURCHASE')
        }

    }
    return <>
        <h1>Czy zakupione produkty</h1>
        <div className="form-field">
            <input type={"checkbox"} onChange={handleChangePurchaseReturned} checked={purchaseReturned}
                   id={"purchaseReturned"}/>
            <label htmlFor='purchaseReturned'>Zostały zwrócone?</label>
        </div>
        <br />
        <hr />
        <br/>
        <div className="form-field">
            <input type={"checkbox"} onChange={handleChangeCompanyPurchase} checked={companyPurchase}  id={"companyPurchase"}/>
            <label htmlFor='companyPurchase'>Zostały kupione na firmę?</label>
        </div>
        <button onClick={handleNext} style={{margin: '40px 0'}}>Dalej</button>

    </>
}

export default PurchaseDetails
