import React, {useContext, useState} from "react";
import {FormDispatchContext, FormStateContext} from "../FormContext";
import {transactionList} from "../transactionList";
import {type} from "@testing-library/user-event/dist/type";

const TransactionDetails = () => {
    const dispatch = useContext(FormDispatchContext);

    const [transactionId, setTransactionId] = useState(transactionList[0].id)

    const handleChange = (e) =>{
        setTransactionId(e.target.value)
    }

    const handleNext = () => {
        dispatch({
            type: 'SET_FORM_DATA',
            payload: {
                transactionId
            }
        })
    }
    return (<>
        <h1>Wybierz Transakcje</h1>
        {transactionList.map(transation => <div style={{margin: '20px 0'}}>
            <input type={"radio"} value={transation.id} name={'transaction'} id={transation.id} onChange={handleChange}/>
            <label htmlFor={transation.id}>{transation.name} - {transation.amount}</label>
        </div>)}
        <button onClick={handleNext} style={{margin: '40px 0'}}>Dalej</button>

    </>)


}

export default TransactionDetails
