        //     type: 'SET_FORM_DATA',
        //     payload: {payoutId: payout}
        // })

      console.log(selectedPayout, payout)
        //go to next step

    }

    return <>
        <h1>Wybierz wypłatę</h1>
        {payoutsList.map((payout, index) => <div style={{margin: '20px 0'}}>
