import React, {createContext, useReducer} from "react";

export const FormStateContext = createContext(null);
export const FormDispatchContext = createContext(null);




const formReducer = (state, action) => {

    switch (action.type) {
        case 'SET_FORM_TYPE_NO_TRANSACTION':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: action.payload
                },
                flow: FLOW_LISTS.noTransaction.normal
            }

        default:
            return state
    }
}
const defaultFormState = {
    data: {
        formType: '',
        purchaseReturned: false,
        companyPurchase: false,
        transactionId: '',
        orderNumber: ''
    },
    currentStep: 0,
    flow: ['PurchaseDetails', 'TransactionDetails', 'Attachment', 'Thank You']
}

const STEPS= {
    PURCHASE_DETAILS: 'PurchaseDetails',
    TRANSACTION_DETAILS: 'TransactionDetails',
    ATTACHMENT: 'Attachment',
    DESCRIPTION: 'Description',
    THANK_YOU: 'ThankYou',
    PURCHASE_INCORRECT: 'PurchaseIncorrect',
    PURCHASE_RETURNED_OR_COMPANY: 'PurchaseReturnedOrCompany',
    ERROR_PURCHASE_RETURNED_OR_COMPANY: 'ErrorPurchaseReturnedOrCompany',
    SELECT_TRANSACTION: 'SelectTransaction'

}
const FLOW_LISTS = {
    noTransaction: {
        normal: [STEPS.PURCHASE_DETAILS, STEPS.TRANSACTION_DETAILS, STEPS.ATTACHMENT, STEPS.THANK_YOU],
        noTransactionPurchaseIncorrect: ['PurchaseIncorrect']
    },
    differentAmount: {
        normal: ['TransactionDetails', 'Attachment', 'Description', 'ThankYou']
    },
    status: {
        normal: ['TransactionDetails', 'Attachment', 'Description', 'ThankYou']
    },
    cashbackRejected: {
        normal: ['PurchaseReturnedOrCompany', 'TransactionDetails', 'Attachment', 'ThankYou'],
        error: ['ErrorPurchaseReturnedOrCompany']
    },
    payout: {
        normal: ['SelectTransaction', 'ShowInfoAboutTransaction', 'ThankYou'],
        askAboutTransaction: ['SelectTransaction', 'ShowInfoAboutTransaction', 'Description', 'ThankYou'],
        noTransactions: ['SelectTransaction', 'Description', 'ThankYou'],
        differentStatus: ['SelectTransaction', 'Description', 'ThankYou']
    },
    other: {
        normal: ['Description','Attachment', 'ThankYou']
    }

}


const FormProvider = ({children}) => {

    const [formState, dispatch] = useReducer(formReducer, defaultFormState)

    return (
        <FormStateContext.Provider value={formState}>
            <FormDispatchContext.Provider value={dispatch}>
                <main style={{padding: '50px 100px', display: 'flex', gap: '80px'}}>
                    <section>
                        {children}
                    </section>
                    <section>
                       <pre>
                        {JSON.stringify(formState, null, 2)}</pre>
                    </section>
                </main>
            </FormDispatchContext.Provider>
        </FormStateContext.Provider>
    )
}


export default FormProvider
