import Name from "./Components/Name";
import Step from "./Components/Step";
import {useState} from "react";
import Phone from "./Components/Phone";
import Agree from "./Components/Agree";
import YouHaveToAgree from "./Components/YouHaveToAgree";


function App() {


    const defaultFormState = {

        currentStep: 0,
        data: {
            name: '',
            phone: '',
            agree1: false,
            agree2: false
        },
        flow: ['hello', 'agree','phone']
    }



    const [formState, setFormState] = useState(defaultFormState)
    const steps = [
        {
            name: 'hello',
            component: Name
        },
        {
            name: 'phone',
            component: Phone
        },
        {
            name: 'youHaveToAgree',
            component: YouHaveToAgree
        },
        {
            name: 'agree',
            component: Agree,
            onSubmit: ()=>{
                if(!formState.data.agree2){
                    setFormState({
                        ...formState,
                        currentStep: 0,
                        flow: ['youHaveToAgree']
                    })
                }
            }
        },
    ]

    return (
        <div className="App" style={{padding: "50px 100px"}}>
            <h1>Elo</h1>
            <Step state={formState} setState={setFormState} steps={steps}/>
        </div>
    );
}

export default App;
