import Step from "./Components/Step";
import {useReducer, useState} from "react";
import BadPurchase1 from "./Components/BadPurchase1";
import BadPurchase2 from "./Components/BadPurchase2";
import BadPurchase3 from "./Components/BadPurchase3";
import PurchaseDetails from "./Components/PurchaseDetails";
import TransactionDetails from "./Components/TransactionDetails";
import Attachment from "./Components/Attatchment";
import ThankYou from "./Components/ThankYou";
import FormProvider from "./FormContext";
import SelectForm from "./Components/SelectForm";


function App() {


    const steps = [
        {
            name: 'PurchaseDetails',
            component: PurchaseDetails,
            onSubmit: (state, setState) => {

                if (state.data.purchaseReturned && state.data.companyPurchase) {
                    setState({
                        ...state,
                        currentStep: 0,
                        flow: ['BadPurchase3']
                    })
                    return
                }
                if (state.data.purchaseReturned) {
                    setState({
                        ...state,
                        currentStep: 0,
                        flow: ['BadPurchase1']
                    })
                    return
                }
                if (state.data.companyPurchase) {
                    setState({
                        ...state,
                        currentStep: 0,
                        flow: ['BadPurchase2']
                    })
                    return
                }

                setState(state)
            }

        },
        {
            name: 'TransactionDetails',
            component: TransactionDetails,
        },
        {
            name: 'Attachment',
            component: Attachment
        },
        {
            name: 'Thank You',
            component: ThankYou
        },
        {
            name: 'BadPurchase1',
            component: BadPurchase1
        },
        {
            name: 'BadPurchase2',
            component: BadPurchase2
        },
        {
            name: 'BadPurchase3',
            component: BadPurchase3
        },

    ]

    return (<>
        <FormProvider>
            <SelectForm/>
        </FormProvider>
    </>)
    //
    // return (
    //     <div className="App" style={{padding: "50px 100px"}}>
    //         <Step state={formState} setState={setFormState} steps={steps}/>
    //     </div>
    // );
}

export default App;
