import React from "react";

const Step = ({state, setState, steps}) => {
    const {currentStep, flow} = state

    const currentStepToRender = steps.find(step => step.name === state.flow[currentStep])
    const handleStateChange = (data) => {
        setState({
            ...state,
            data: {
                ...state.data,
                ...data
            }
        })
    }

    const handleChangeFlow = (newFlow) => {
        setState(
            {
                ...state,
                flow: newFlow
            }
        )
    }
    const renderCurrentStep = () => {

        const Component = currentStepToRender.component
        return <Component state={state} onChange={handleStateChange} changeFlow={handleChangeFlow}/>
    }

    const handleNextBtnClick = () => {
        let newState = state

        if('onSubmit' in currentStepToRender){
             currentStepToRender.onSubmit(state, setState)
        }




    }






    const handlePrevBtnClick = () => {
        setState({
            ...state,
            currentStep: currentStep - 1
        });
    }
    return <>
        <br/>
        <br/>
        <br/>

        <pre>
        {JSON.stringify({...state}, null, 2)}
        </pre>
        <br/>
        <br/>
        <br/>
        <pre>
        </pre>
        <br/>
        {currentStep + 1} z {flow.length}
        <br/>
        {renderCurrentStep()}
        <div style={{display:'flex', gap:"40px", marginTop: "20px"}}>
        {currentStep > 0 && <button onClick={handlePrevBtnClick}>WroÄ‡ </button>}
        <br/>
        {currentStep < flow.length - 1 && <button onClick={handleNextBtnClick}>Dalej </button>}
        </div>
    </>
}

export default Step
