import React from "react";

const Step = ({state, setState, steps}) => {

    const currentStepToRender = steps.find(step => step.name === state.flow[state.currentStep])
    const handleStateChange = (data) => {
        setState({
            ...state,
            data: {
                ...state.data,
                ...data
            }
        })
    }

    const renderCurrentStep = () => {

        const Component = currentStepToRender.component
        return <Component state={state} onChange={handleStateChange} />
    }

    const handleNextBtnClick = () => {

        if('onSubmit' in currentStepToRender){
             currentStepToRender.onSubmit({
                 ...state,
                 currentStep: state.currentStep + 1
             }, setState)
        }

        else {
            setState({
                ...state,
                currentStep: state.currentStep + 1
            })
        }

    }

    const handlePrevBtnClick = () => {
        setState({
            ...state,
            currentStep: state.currentStep - 1
        });
    }
    return <>


        <pre>
        {JSON.stringify({...state}, null, 2)}
        </pre>

<hr />
        {renderCurrentStep()}

        <div style={{display:'flex', gap:"40px", marginTop: "20px"}}>
        {state.currentStep > 0 && <button onClick={handlePrevBtnClick}>WroÄ‡ </button>}


        {state.currentStep  < state.flow.length -1 && <button onClick={handleNextBtnClick}>Dalej </button>}
        </div>
    </>
}

export default Step
