import React from "react";

const Step = ({state, setState})=> {
    const {currentStep, flow} = state

    const renderCurrentStep = () => {
        const stepsToRender = state.steps.find(step => step.name === flow[currentStep])
    }

    const handleNextBtnClik = () => {
        setState({
            ...state,
            steps: {...state.steps},
            flow: [...state.flow],
            currentStep: currentStep + 1
        });
    }

    const handlePrevBtnClik = () => {
        setState({
            ...state,
            steps: {...state.steps},
            flow: [...state.flow],
            currentStep: currentStep - 1
        });
    }
    return <>
        <br />
        <br />
        <br />

        <pre>

        {JSON.stringify(state, null, 2)}
        </pre>
        <br />
        {currentStep +1} z {flow.length}
        <br />
        <br />
        {currentStep >0 && <button onClick={handlePrevBtnClik}>WroÄ‡ </button>}
        <br />
        {currentStep < flow.length -1 && <button onClick={handleNextBtnClik}>Dalej </button>}
    </>
}

export default Step
