import Name from "./Components/Name";
import Step from "./Components/Step";
import {useState} from "react";
import Phone from "./Components/Phone";
import Agree from "./Components/Agree";
import YouHaveToAgree from "./Components/YouHaveToAgree";


function App() {


    const defaultFormState = {

        currentStep: 0,
        data: {
            name: '',
            phone: '',
            agree1: false,
            agree2: false
        },
        flow: ['hello', 'agree','phone']
    }



    const [formState, setFormState] = useState(defaultFormState)

    const showOnlyFinalStep = ( state, setState, stepName) => {
        setState({
            ...state,
            currentStep: 0,
            flow: [stepName]
        })
    }

    const insertStepAsNext = ( state, setState, stepName) => {
        const insertAt = state.currentStep
        setState({
            ...state,
            flow:[
                ...state.flow.slice(0, insertAt),
                stepName,
                ...state.flow.slice(insertAt)
            ]
        })
    }
    const steps = [
        {
            name: 'hello',
            component: Name
        },
        {
            name: 'phone',
            component: Phone
        },
        {
            name: 'youHaveToAgree',
            component: YouHaveToAgree
        },
        {
            name: 'agree',
            component: Agree,
            onSubmit: (state, setState) => {
                if(!state.data.agree1){
                    showOnlyFinalStep(state, setState, 'youHaveToAgree')
                }
                else if(!state.data.agree2){
                    insertStepAsNext(state,setState, 'youHaveToAgree')
                }
                else{
                    setState(state)
                }
            }
        },
    ]

    return (
        <div className="App" style={{padding: "50px 100px"}}>
            <h1>Elo</h1>
            <Step state={formState} setState={setFormState} steps={steps}/>
        </div>
    );
}

export default App;
