import React, {createContext, useReducer} from "react";

export const FormStateContext = createContext(null);
export const FormDispatchContext = createContext(null);


const formReducer = (state, action) => {

    switch (action.type) {
        case 'SET_FORM_TYPE_NO_TRANSACTION':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: 'no-transaction'
                },
                flow: flowList.noTransaction.normal
            }
        case 'SET_FORM_TYPE_DIFFERENT_AMOUNT':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: 'different-amount'
                },
                flow: flowList.differentAmount.normal
            }
        case 'SET_FORM_TYPE_STATUS':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: 'status'
                },
                flow: flowList.status.normal
            }
        case 'SET_FORM_TYPE_CASHBACK_REJECTED':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: 'cashback-rejected'
                },
                flow: flowList.cashbackRejected.normal
            }
        case 'SET_FORM_TYPE_PAYOUT':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: 'payout'
                },
                flow: flowList.payout.normal
            }
        case 'SET_FORM_TYPE_OTHER':
            return {
                ...state,
                data: {
                    ...state.data,
                    formType: 'other'
                },
                flow: flowList.other.normal
            }
        case 'GO_TO_NEXT_STEP':
            return {
                ...state,
                currentStep: state.currentStep + 1
            }

        default:
            return state
    }
}
const FormProvider = ({children}) => {

    const [formState, dispatch] = useReducer(formReducer, defaultFormState)

    return (
        <FormStateContext.Provider value={formState}>
            <FormDispatchContext.Provider value={dispatch}>
                <main style={{padding: '50px 100px', display: 'flex', gap: '80px'}}>
                    <section>
                        {children}
                    </section>
                    <section>
                       <pre>
                        {JSON.stringify(formState, null, 2)}</pre>
                    </section>
                </main>
            </FormDispatchContext.Provider>
        </FormStateContext.Provider>
    )
}


const STEPS = {
    SELECT_REASON: 'SelectReason',
    PURCHASE_DETAILS: 'PurchaseDetails',
    TRANSACTION_DETAILS: 'TransactionDetails',
    ATTACHMENT: 'Attachment',
    DESCRIPTION: 'Description',
    THANK_YOU: 'ThankYou',
    PURCHASE_INCORRECT: 'PurchaseIncorrect',
    PURCHASE_RETURNED_OR_COMPANY: 'PurchaseReturnedOrCompany',
    ERROR_PURCHASE_RETURNED_OR_COMPANY: 'ErrorPurchaseReturnedOrCompany',
    SELECT_TRANSACTION: 'SelectTransaction',
    SHOW_INFO_ABOUT_TRANSACTION: 'ShowInfoAboutTransaction'

}
const flowList = {

    noTransaction: {
        normal: [
            STEPS.SELECT_REASON,
            STEPS.PURCHASE_DETAILS,
            STEPS.TRANSACTION_DETAILS,
            STEPS.ATTACHMENT,
            STEPS.THANK_YOU
        ],
        noTransactionPurchaseIncorrect: [
            STEPS.PURCHASE_INCORRECT
        ]
    },
    differentAmount: {
        normal: [STEPS.SELECT_REASON, STEPS.TRANSACTION_DETAILS, STEPS.ATTACHMENT, STEPS.DESCRIPTION, STEPS.THANK_YOU]
    },
    status: {
        normal: [STEPS.SELECT_REASON, STEPS.TRANSACTION_DETAILS, STEPS.ATTACHMENT, STEPS.DESCRIPTION, STEPS.THANK_YOU]
    },
    cashbackRejected: {
        normal: [STEPS.SELECT_REASON, STEPS.PURCHASE_RETURNED_OR_COMPANY, STEPS.TRANSACTION_DETAILS, STEPS.ATTACHMENT, STEPS.THANK_YOU],
        error: [STEPS.ERROR_PURCHASE_RETURNED_OR_COMPANY]
    },
    payout: {
        normal: [STEPS.SELECT_REASON, STEPS.SELECT_TRANSACTION, STEPS.SHOW_INFO_ABOUT_TRANSACTION, STEPS.THANK_YOU],
        askAboutTransaction: [STEPS.SELECT_REASON, STEPS.SELECT_TRANSACTION, STEPS.SHOW_INFO_ABOUT_TRANSACTION, STEPS.DESCRIPTION, STEPS.THANK_YOU],
        noTransactions: [STEPS.SELECT_REASON, STEPS.SELECT_TRANSACTION, STEPS.DESCRIPTION, STEPS.THANK_YOU],
        differentStatus: [STEPS.SELECT_REASON, STEPS.SELECT_TRANSACTION, STEPS.DESCRIPTION, STEPS.THANK_YOU]
    },
    other: {
        normal: [STEPS.SELECT_REASON, STEPS.DESCRIPTION, STEPS.ATTACHMENT, STEPS.THANK_YOU]
    }

}

const defaultFormState = {
    data: {
        formType: '',
        purchaseReturned: false,
        companyPurchase: false,
        transactionId: '',
        orderNumber: ''
    },
    currentStep: 0,
    flow: flowList.noTransaction.normal
}
export default FormProvider
